import { Component } from '@angular/core';
import { VotingService } from '../voting.service';

@Component({
  selector: 'app-voting-system',
  templateUrl: './voting-system.component.html',
  styleUrls: ['./voting-system.component.scss']
})
export class VotingSystemComponent {
  count:number=0;
constructor(
  private voteService:VotingService
){}

onSubmit(){
  const formData={
    userId:(<HTMLInputElement>document.getElementById("userid")).value
  }
  this.voteService.vote(formData.userId);
  this.count=this.voteService.getVoteCount();
}
}
<div>
    <Label>Enter ID:</Label> <br>
    <input type="text" name="userid" id="userid" placeholder="Enter ID...">
</div>
<h1>Total Votes: {{count}}</h1>
