import { Component } from '@angular/core';

import { Router } from '@angular/router';
import { GetqouteService } from 'src/app/services/getqoute.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-personal-detail',
  templateUrl: './personal-detail.component.html',
  styleUrls: ['./personal-detail.component.scss']
})
export class PersonalDetailComponent {

  status:boolean=false;
  isButtonVisible = false;

  constructor( 

    private getQouteService:GetqouteService,
    private router: Router,
    private userService:UserService
  ){}

  ngOnInit(): void {
  this.validateCnic()

  }



  validateCnic(): void {

    const formData = {
      cnic: (<HTMLInputElement>document.getElementById("cnic")).value, 
      name:(<HTMLInputElement>document.getElementById("name")).value,
      verificationDate:(<HTMLInputElement>document.getElementById("dateofverification")).value,
      verificationStatus:(<HTMLInputElement>document.getElementById("statusofverification")).value,


    };

    this.getQouteService.getPersonalDetailsByCnic(formData.cnic).subscribe((Response) => {

      if (Response.status == 201 || Response.status==200) {
        console.log(Response["body"]);
        this.isButtonVisible=true;
      }
      else{
        this.isButtonVisible=false;

      }
    });

  }
  onSubmit(){
    const formData = {
      cnic: (<HTMLInputElement>document.getElementById("cnic")).value, 
      name:(<HTMLInputElement>document.getElementById("name")).value,
      verificationDate:(<HTMLInputElement>document.getElementById("dateofverification")).value,
      verificationStatus:(<HTMLInputElement>document.getElementById("statusofverification")).value,
      email:this.userService.getUserEmail,

    };
    this.getQouteService.addPersonalDetails(formData).subscribe((Response) => {
      console.log(Response);
      if (Response.status == 201 || Response.status==200) {
      this.router.navigateByUrl('/adddriver');
      }});
  }

}


<div class="col why-us-lead-form">
    <div class="lead-form">
      <h1>National Data Verification</h1>
      <form>
        <div class="input-field">
          <label for="cnic">CNIC</label>
          <input type="text" name="cnic" id="cnic" (input)="validateCnic()" placeholder="CNIC" required>
        </div>
        <div class="input-field">
          <label for="name">Full Name</label>
          <input type="text" name="name" id="name" placeholder="Full Name" required>
        </div>
  
        <div class="input-field">
          <label for="dateofverification">Date of Verification</label>
          <input type="text" name="dateofverification" id="dateofverification" placeholder="Date of Verification" required>
        </div>
  
        <div class="input-field">
          <label for="statusofverification">Status of Verification</label>
          <input type="text" name="statusofverification" id="statusofverification" placeholder="Status of Verification" required>
        </div>
  
        <button mat-raised-button color="primary" (click)="onSubmit()" *ngIf="isButtonVisible">Next</button>
      </form>
    </div>
  </div>
