import { Component } from '@angular/core';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-client-dashboard',
  templateUrl: './client-dashboard.component.html',
  styleUrls: ['./client-dashboard.component.scss']
})
export class ClientDashboardComponent {
  currentContent: string = 'backup';
  userEmail: string = '';
  userRoles: string[] = [];
  constructor(private userService: UserService) { }
  ngOnInit(): void {
    this.userEmail = this.userService.getUserEmail();
    this.userRoles = this.userService.getUserRoles();
    console.log("Email", this.userEmail);
    console.log("userRoles", this.userRoles);

    this.loadContent();

  }

  loadContent() {
    // You can fetch data from your service here if needed
    // Example: this.dataService.getBackupData().subscribe(data => { /* process data */ });
  }

  showContent(content: string) {
    this.currentContent = content;
    this.loadContent(); // Load content when the user switches
  }
}


<div class="container">
  <div class="sidebar">
    <h2>Client Dashboard</h2>
    <div class="nav">
      <a href="#" (click)="showContent('backup')">Backup Database</a>
      <a href="#" (click)="showContent('vehicles')">Vehicles List</a>
      <!-- Add more links with corresponding content values -->
      <a href="#" (click)="showContent('logout')">Logout</a>
      <a href="#" (click)="showContent('agent-setting')">Agent Setting</a>
    </div>
  </div>
  <div class="content">
    <h1>Welcome, Client!</h1>
    <div *ngIf="currentContent === 'backup'">
      <!-- Backup Database content goes here -->
      <h2>Backup Database</h2>
      <p>Click the button below to create a backup of the database.</p>
      <button (click)="backupDatabase()">Backup Database</button>
    </div>
    <div *ngIf="currentContent === 'vehicles'">
      <!-- Vehicles List content goes here -->
      <div id="vehicles">
        <h2>Vehicles List</h2>
        <table>
          <thead>
            <tr>
              <th>Vehicle ID</th>
              <th>Make</th>
              <th>Model</th>
              <th>Year</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>Toyota</td>
              <td>Corolla</td>
              <td>2022</td>
            </tr>
            <tr>
              <td>2</td>
              <td>Honda</td>
              <td>CR-V</td>
              <td>2021</td>
            </tr>
            <!-- Add more vehicle rows here -->
          </tbody>
        </table>
      </div>
      <!-- Add your table and vehicle data here -->
    </div>
    <!-- Add more content sections as needed -->
  </div>
</div>
